file 'style.css'
file 'shadow.js'

chat_theme 'gtao' {
    styleSheet = 'style.css',
    script = 'shadow.js',
    msgTemplates = {
        default = '<div class="default"><b>{0}:</b><span> {1}</span></div>',
        ogloszenie = '<div class="ogloszenie"><span><b>{0}</b></span><span><BR>{1}</span><div class="icon"><img src="data:image/png;base64,"></div></div>',
        tweet = '<div class="tweet"><span><b>@{0}</b></span><BR><span>{1}</span><div class="icon"><img src="data:image/png;base64,"></div></div>',
        med = '<div class="med"><span><b>{0}</b></span><BR><span>{1}</span><div class="icon"><img src="data:image/png;base64,"></div></div>',
        localooc = '<div class="localooc"><span><b>{0}</b></span><BR><span>{1}</span><div class="icon"><img src="data:image/png;base64,"></div></div>',
        ooc = '<div class="ooc"><span><b>{0}</b></span><BR><span>{1}</span><div class="icon"><img src="data:image/png;base64,"></div></div>',
        me = '<div class="me"><span><b>{0}</b></span><BR><span>{1}</span><div class="icon"><img src="data:image/png;base64,"></div></div>'

    }
}